import React from 'react';
import { Button, Form, Grid, Header, Segment } from 'semantic-ui-react';
import Barrasuperior from './Barrasuperior'
import BackgroundSlider from 'react-background-slider'
import image1 from '../img/login/slide-img-1.jpg'
import image2 from '../img/login/slide-img-2.jpg'
import image3 from '../img/login/slide-img-3.png'
import './Login.css'
//import { Redirect } from 'react-router-dom'
import './Login.css';
import { userService } from './logica/_services';

class Login extends React.Component {

    constructor(props) {
        super(props);

        userService.logout();

        this.state = {
            username: '',
            password: '',
            submitted: false,
            loading: false,
            error: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { username, password, returnUrl } = this.state;

        // stop here if form is invalid
        if (!(username && password)) {
            return;
        }

        this.setState({ loading: true });
        userService.login(username, password)
            .then(
                user => {
                    const { from } = this.props.location.state || { from: { pathname: "/" } };
                    this.props.history.push(from);
                },
                error => this.setState({ error, loading: false })
            );
    }

    render() {
        const { username, password, submitted, loading, error } = this.state;
        return (
            <div>
                <Barrasuperior />
                <div className="Login">
                    <BackgroundSlider images={[image2, image1, image3]}
                        duration={5} transition={2} />
                    <Grid textAlign="center" verticalAlign="middle">
                        <Grid.Column style={{ maxWidth: 400 }}>
                            <Segment>
                                <Header as="h2" textAlign="center">
                                    Ingreso a Fablab Inventario
                        </Header>
                                <Form size="large" onSubmit={this.handleSubmit} >
                                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                                    <Form.Input
                                        fluid
                                        icon="user"
                                        iconPosition="left"
                                        placeholder="Usuario"
                                    />
                                        {submitted && !username &&
                                            <div className="help-block">Username is required</div>
                                        }
                                    </div>
                                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                                    <Form.Input
                                        fluid
                                        icon="lock"
                                        iconPosition="left"
                                        placeholder="ContraseÃ±a"
                                        type="password"
                                    />
                                        {submitted && !password &&
                                            <div className="help-block">Password is required</div>
                                        }
                                    </div>
                                    <Button color="instagram" fluid size="large"  >
                                        Ingresar
                                </Button>
                                    {error &&
                                        <div className={'alert alert-danger'}>{error}</div>
                                    }
                                </Form>
                            </Segment>
                        </Grid.Column>
                    </Grid>
                </div>
            </div>
        );
    }
}
export { Login };
