import React, { useEffect } from 'react'
import { ResponsiveBar } from '@nivo/bar'


import data from './DataBar'
import config from './ConfigBar'

import '../Chart.css'
import { getDatosGraficosBarra } from '../../firebase'

function Bar(){

    useEffect(() => {
        var tiempo = new Date();

        getDatosGraficosBarra(tiempo.getFullYear(), tiempo.getMonth())

    }, [])
    return (
        <div class="chart" >
            <ResponsiveBar
                data={data}
                keys={[
                    'hot dog',
                    'wea',
                    'sandwich',
                    'kebab',
                    'fries',
                    'donut'
                ]}
                indexBy="productos"
                margin={config.margin}
                padding={0.3}
                groupMode="grouped"
                colors={['Crimson',
                    '#ffae00',
                    'Crimson',
                    '#0066ff',
                    '#9000ff',
                    '#FFCA28']}
                colorBy="id"
                defs={config.defs}
                borderWidth={2}
                borderColor={{ from: 'color', modifiers: [['darker', '1.6']] }}
                axisTop={null}
                axisRight={null}
                axisBottom={config.axisBottom}
                axisLeft={config.axisLeft}
                labelSkipWidth={12}
                labelSkipHeight={12}
                labelTextColor={{ from: 'color', modifiers: [['darker', 1.6]] }}
                legends={config.legends}
                animate={true}
                onMouseOver={() => this.props.onMouseOverCallback()}
                onMouseOut={() => this.props.onMouseOutCallback(null)}
                motionStiffness={90}
                motionDamping={15}
            />
        </div>
    )
}
export default Bar;
