import React, { useState, useEffect } from 'react';
import { Card, Grid, Button } from 'semantic-ui-react'


function Crum() {

    const options = [
        { key: "1", text: "Entrega de Productos", value: "1" },
        { key: "2", text: "Devoluci贸n de Productos", value: "2" },
        { key: "3", text: "Actualizar Stock", value: "3" },
        { key: "4", text: "Agregar nuevo Producto", value: "4" },
    ]
    const [isEntrega, setEntrega] = useState(false);
    const [isDevolucion, setIsDevolucion] = useState(false);
    const [isActualizar, setIsActualizar] = useState(false);
    const [isAgregar, setIsAgregar] = useState(false);
    const [eleccion, setEleccion] = useState(true);


    return (
        <div>
            <Card fluid>
                <Card.Content>
                    {eleccion && 
                    <Grid  centered>
                            <Button.Group fluid>
                            <Button onClick={e => {setEleccion(!eleccion); setEntrega(!isEntrega)}} >Entrega de productos</Button>
                            <Button onClick={e => { setEleccion(!eleccion); setIsDevolucion(!isDevolucion) }} >Devoluci贸n de productos</Button>
                            <Button onClick={e => { setEleccion(!eleccion); setIsActualizar(!isActualizar) }} >Actualizar Stock</Button>
                            <Button onClick={e => { setEleccion(!eleccion); setIsAgregar(!isAgregar) }} >Agregar nuevo producto</Button>
                            </Button.Group>
                    </Grid>
                    }
                    {isEntrega && <div> Entrega de producto
                        <Button onClick={e => { setEleccion(!eleccion); setEntrega(!isEntrega);  }} >
                            Back
                            </Button>
 </div>}
                    {isDevolucion && <div> Devoluci贸n de producto <Button onClick={e => { setEleccion(!eleccion); setIsDevolucion(!isDevolucion); }} >
                        Back
                            </Button> </div>}
                    {isActualizar && <div> Actualizar Stock <Button onClick={e => { setEleccion(!eleccion); setIsActualizar(!isActualizar); }} >
                        Back
                            </Button> </div>}
                    {isAgregar && <div> Agregar nuevo producto <Button onClick={e => { setEleccion(!eleccion); setIsAgregar(!isAgregar); }} >
                        Back
                            </Button> </div>}
                </Card.Content>
            </Card>

        </div>
    );
}

export default Crum;
