import React, {useState} from 'react';
import { Button, Form, Grid, Header, Segment } from 'semantic-ui-react';
import Barrasuperior from './Barrasuperior'
import BackgroundSlider from 'react-background-slider'
import image1 from '../img/login/slide-img-1.jpg'
import image2 from '../img/login/slide-img-2.jpg'
import image3 from '../img/login/slide-img-3.png'
import './Login.css'
//import { Redirect } from 'react-router-dom'
import { apiRequest, validateLoginForm } from "../verificacion/Helpers.ts";
import useErrorHandler from "../verificacion/ErrorHandler";

function Login() {

    const [user, setUser] = React.useState("");
    const [userPassword, setUserPassword] = React.useState("");
    const [loading, setLoading] = React.useState(false);
    const { error, showError } = useErrorHandler(null);
    const authHandler = async () => {
        try {
            setLoading(true);
            const userData = await apiRequest(
                "https://jsonplaceholder.typicode.com/users",
                "post",
                { email: user, password: userPassword }
            );
            const { id, email } = userData;
        } catch (err) {
            setLoading(false);
            showError(err.message);
        }
    };

        return (
            <div>
                <Barrasuperior/>
                <div className="Login">
                    <BackgroundSlider images={[image2, image1, image3]}
                        duration={5} transition={2} />
                    <Grid textAlign="center" verticalAlign="middle">
                        <Grid.Column style={{ maxWidth: 400 }}>
                            <Segment>
                                <Header as="h2" textAlign="center">
                                    Ingreso a Fablab Inventario
                        </Header>
                                <Form size="large" onSubmit={e => {
                                    e.preventDefault();
                                    // Auth handler 
                                }}  >
                                    <Form.Input
                                        fluid
                                        icon="user"
                                        iconPosition="left"
                                        placeholder="Usuario"
                                        value={user}
                                        onChange={e => setUser(e.target.value)}  

                                    />

                                    <Form.Input
                                        fluid
                                        icon="lock"
                                        iconPosition="left"
                                        placeholder="Contraseña"
                                        type="password"
                                        value={userPassword}
                                        onChange={e => setUserPassword(e.target.value)} 
                                    />

                                    <Button color="instagram" fluid size="large" type="submit" disabled={loading} block={true} >
                                        {loading ? "Loading..." : "Sign In"}
                                </Button>

                                </Form>
                            </Segment>
                        </Grid.Column>
                    </Grid>
                </div>
            </div>
        );
}
export default Login ;
