import React, { useState, useEffect} from 'react';
import { Button, Form, Grid, Header, Segment } from 'semantic-ui-react';
import Barrasuperior from './Barrasuperior'
import BackgroundSlider from 'react-background-slider'
import image1 from '../img/login/slide-img-1.jpg'
import image2 from '../img/login/slide-img-2.jpg'
import image3 from '../img/login/slide-img-3.png'
import './Login.css'
//import { Redirect } from 'react-router-dom'


function Login() {

    const [user, setUser] = useState("");
    const [userPassword, setUserPassword] = useState("");
    const [loading, setLoading] = useState(false);
    const [hasError, setErrors] = useState(false)

    useEffect(() =>
        fetch("https://jsonplaceholder.typicode.com/users")
            .then(res => res.json())
            .then(res => this.setState({ user: res }))
            .catch(() => this.setState({ hasErrors: true }))
    );



        return (
            <div>
                <Barrasuperior/>
                <div className="Login">
                    <BackgroundSlider images={[image2, image1, image3]}
                        duration={5} transition={2} />
                    <Grid textAlign="center" verticalAlign="middle">
                        <Grid.Column style={{ maxWidth: 400 }}>
                            <Segment>
                                <Header as="h2" textAlign="center">
                                    Ingreso a Fablab Inventario
                        </Header>
                                <Form size="large" onSubmit={e => {
                                    e.preventDefault();
                                    // Auth handler 
                                }}  >
                                    <Form.Input
                                        fluid
                                        icon="user"
                                        iconPosition="left"
                                        placeholder="Usuario"
                                        value={user}
                                        onChange={e => setUser(e.target.value)}  

                                    />

                                    <Form.Input
                                        fluid
                                        icon="lock"
                                        iconPosition="left"
                                        placeholder="Contraseña"
                                        type="password"
                                        value={userPassword}
                                        onChange={e => setUserPassword(e.target.value)} 
                                    />

                                    <Button color="instagram" fluid size="large" type="submit" disabled={loading} block={true} >
                                        {loading ? "Loading..." : "Sign In"}
                                </Button>

                                </Form>
                            </Segment>
                            <div>
                                {JSON.stringify(user)}
                            </div>
                        </Grid.Column>
                    </Grid>
                </div>
            </div>
        );
}
export default Login ;
