import React, { useState, useMemo} from 'react';
import { Button, Form, Grid, Input, TextArea, Card,Modal, Header,Table, Icon } from 'semantic-ui-react';
import Productselect from './ListaProductoDevolucion'
import { UserContext } from '../UseContext';

function DevolucionProducto() {




    const [productSelect, setProductSelect] = useState([]);


    const [productos] = useState([
        { id: 0, stock: 5, name: 'Arduino' },
        { id: 1, stock: 7, name: 'Impresora' },
        { id: 2, stock: 12, name: 'Sensor Ultrasonido' },
        { id: 3, stock: 32, name: 'Led Rojo' },
        { id: 4, stock: 50, name: 'Resistencias' },
        { id: 37, stock: 50, name: 'Resistencias' },
        { id: 8, stock: 50, name: 'Resistencias' },
        { id: 9, stock: 10, name: 'Protoboard' }]);

    const [isShowingModal, setIsShowingModal] = useState(true);
    const [isclose, setIsclose] = useState(true);

    const [encargado, setEncargado] = useState("");
    const [descripcion, setDescripcion] = useState("");
    const [file, setFile] = useState('');
    const [nameFile, setNameFile] = useState('');

    const providerValue = useMemo(() => ({ productSelect, setProductSelect}), [productSelect, setProductSelect]);

    const fileInputRef = React.createRef();

    const getFile = e => {
        setFile(e.target.files[0])
        setNameFile(e.target.files[0].name)
    }

    const handleSubmit = (evt) => {
        alert(encargado+descripcion+file)
    }

    const verArticulos = (evt) =>{
        return(
        <Modal>
            <Modal.Header>Profile Picture</Modal.Header>
            <Modal.Content scrolling>
                <Modal.Description>
                    <Header>Modal Header</Header>
                        {productSelect.map((item, index) => {
                        const {producto, stock, cantidad} = item
                        return (
                            <p>Producto: {producto}  Disponibilidad: {stock}  Cantidad Pedida: {cantidad}</p>
                            )
                    })
                    }}

                </Modal.Description>
            </Modal.Content>
            <Modal.Actions>
                <Button primary>
                    Salir <Icon name='chevron right' />
                </Button>
            </Modal.Actions>
        </Modal>
        )
    }

    const confirmarPedido = (evt) => {

        setIsShowingModal(!isShowingModal);
        setIsclose(!isclose);

    }


    return (
        <UserContext.Provider value={providerValue}>
        <Card fluid>
            <Card.Content>
                <Grid columns="equal">
                    <Grid.Column>
                        {productSelect.length}
                        { isShowingModal ?

                            < Modal open={isclose} trigger={< Button fluid color="youtube" > Lista de Productos</Button >} size="small" >
                                <Modal.Header>Devolucion Producto</Modal.Header>
                                <Modal.Content >
                                    <Modal.Description>
                                        <Form>
                                            <Header>Lista de Productos</Header>
                                            <Table>
                                                <Table celled>
                                                    <Table.Header>
                                                        <Table.Row>
                                                            <Table.HeaderCell>Producto  </Table.HeaderCell>
                                                            <Table.HeaderCell>Articulos Pendientes</Table.HeaderCell>
                                                            <Table.HeaderCell>Cantidad</Table.HeaderCell>
                                                            <Table.HeaderCell>Seleccionar</Table.HeaderCell>
                                                        </Table.Row>
                                                    </Table.Header>
                                                    <Table.Body>
                                                        {productos.map((producto, index) => {
                                                            const { stock, name, id } = producto
                                                            return (
                                                                <Productselect name={name} stock={stock} id={id} ></Productselect>
                                                            )
                                                        })}
                                                    </Table.Body>
                                                </Table>
                                            </Table>
                                                <Button className="close" onClick={e => confirmarPedido()} color="vk" circular type="submit" >
                                                Confirmar <Icon name='chevron right' />
                                            </Button>
                                        </Form>
                                    </Modal.Description>
                                </Modal.Content>
                            </Modal >
                        : 
                        <div style={{textAlign:"center"}} >

                            <Button onClick={e => verArticulos}  icon="arrow alternate circle up" circular color="blue" attached='left'>Ver Articulos</Button>
                            <Button onClick={ e => {setIsShowingModal(!isShowingModal); setProductSelect([])}} icon="delete" color="red" circular attached='right'>Eliminar Articulos</Button>
                        </div>

                    }
                    </Grid.Column>
                    <Grid.Column>
                        <Input fluid name="encargado" placeholder="Encargado de Devolucion" onChange={e => setEncargado(e.target.value)} />
                    </Grid.Column>

                    <Grid.Column width={1}>
                        <Button icon="upload" onClick={() => fileInputRef.current.click()} />
                        <input ref={fileInputRef} type="file" hidden onChange={getFile} />
                        {nameFile}
                    </Grid.Column>
                </Grid>
            </Card.Content>
            <Form>
                <Card.Content extra>
                    <TextArea name="detalle" placeholder='Detalle de la Devolucion' onChange={e => setDescripcion(e.target.value)} />
                    <Button color="facebook" type="submit" size="medium" onClick={handleSubmit} >
                        Agregar
                        </Button>
                </Card.Content>
            </Form>
            {/* <Button color="facebook" type="submit" onClick={this.handleSubmit()} > */}
        </Card>
        </UserContext.Provider>
    );
}
export default DevolucionProducto;
