import React, {useState} from 'react'
import { Button, Modal, Header, Icon, Checkbox, Form, Table } from 'semantic-ui-react'

function ListaDevolucion({name, stock, id}) {

    const [banderaSelect, setBandera] = useState(true)

    const validacion = (evt, articulo, id, stockDisponible) => {
        alert(`ID: ${id}\nArticulo: ${articulo}\nStock: ${stockDisponible}\nPedido: ${evt}`)

    }
    const eliminar = (mensaje) => {
        alert(mensaje);
    }
    const inputRef = React.createRef();


        return (
            <Table.Row>
                <Table.Cell textAlign="center">{name}</Table.Cell>
                <Table.Cell textAlign="center">{stock}</Table.Cell>
                <Table.Cell textAlign="center">
                    <input id={id} type="number" name="hola" defaultValue={0} ref={inputRef} ></input>
                </Table.Cell>

                <Table.Cell textAlign="center" >
                    {banderaSelect ? 
                    <Button circular icon='check circle outline' onClick={e => {validacion(inputRef.current.value, name, id, stock); setBandera(!banderaSelect)}} /> 
                    :
                    <Button circular icon='delete' onClick={e => { eliminar("Eliminado"); setBandera(!banderaSelect) }} /> 

                    }

                    {/* <Checkbox toggle onMouseUp={e => validacion(inputRef.current.value, name, id, stock)}  onMouseDown={e => {;elimnar("eliminado")}}></Checkbox> */}
                </Table.Cell>
                {/* <div>{document.getElementById("hola").v}</div> */}
            </Table.Row>
           )
    }

export default ListaDevolucion
