import React, {useState} from 'react'
import { Button, Message, Table } from 'semantic-ui-react'

function ListaDevolucion({name, stock, id}) {

    const [banderaSelect, setBanderaSelect] = useState(true);
    const [stockBandera, setStockBandera] = useState(true)

    const validacion = (evt, articulo, id, stockDisponible) => {

        if (Number(evt)>Number(stockDisponible)) {
            setStockBandera(false)
        }


        alert(`ID: ${id}\nArticulo: ${articulo}\nStock: ${stockDisponible}\nPedido: ${evt}`)

    }
    const eliminar = (mensaje) => {
        setBanderaSelect(!banderaSelect)
        alert(mensaje);
    }
    const inputRef = React.createRef();


        return (

            <Table.Row>
                <Table.Cell textAlign="center">{name}</Table.Cell>
                <Table.Cell textAlign="center">{stock}</Table.Cell>
                <Table.Cell textAlign="center">
                    {stockBandera ? <div /> :
                        <Message negative>
                            <Message.Header>La petici√≥n realizada es mayor al stock disponible</Message.Header>
                        </Message>
                    }
                    <input id={id} type="number" name="hola" defaultValue={0} ref={inputRef} ></input>
                </Table.Cell>

                <Table.Cell textAlign="center" >
                    {banderaSelect ? 
                    <Button circular color="green" icon='check circle outline' onClick={e => {validacion(inputRef.current.value, name, id, stock); }} /> 
                    :
                    <Button circular color="red" icon='delete' onClick={e => { eliminar("Eliminado");  }} /> 

                    }

                    {/* <Checkbox toggle onMouseUp={e => validacion(inputRef.current.value, name, id, stock)}  onMouseDown={e => {;elimnar("eliminado")}}></Checkbox> */}
                </Table.Cell>
                {/* <div>{document.getElementById("hola").v}</div> */}
            </Table.Row>
           )
    }

export default ListaDevolucion
