import React, { useState}/*, useEffect } */from 'react';
import { Button, Form, Grid, Input, TextArea, Card } from 'semantic-ui-react';
import Productselect from './ListaProductoDevolucion'

function DevolucionProducto() {


    const [listaProductos, setListaProductos] = useState([]);
    const [encargado, setEncargado] = useState("");
    const [file, setFile] = useState('');
    const [nameFile, setNameFile] = useState('');


    const fileInputRef = React.createRef();

    const getFile = e => {
        setFile(e.target.files[0])
        setNameFile(e.target.files[0].name)
    }


    return (
        <Card fluid>
            <Card.Content>
                <Grid columns="equal">
                    <Grid.Column>
                        <Productselect />
                    </Grid.Column>

                    <Grid.Column>
                        <Input fluid name="encargado" placeholder="Encargado de Devolucion" />
                    </Grid.Column>

                    <Grid.Column width={1}>
                        <Button icon="upload" onClick={() => fileInputRef.current.click()} />
                        <input ref={fileInputRef} type="file" hidden onChange={getFile} />
                        {nameFile}
                    </Grid.Column>
                </Grid>
            </Card.Content>
            <Form>
                <Card.Content extra>
                    <TextArea name="detalle" placeholder='Detalle de la Devolucion' />
                    <Button color="facebook" type="submit" size="medium" >
                        Agregar
                        </Button>
                </Card.Content>
            </Form>
            {/* <Button color="facebook" type="submit" onClick={this.handleSubmit()} > */}


        </Card>
    );
}
export default DevolucionProducto;
