import React from 'react'
import { Item, Label, Header, Segment, Grid, Button } from 'semantic-ui-react'


function ListarRegistros({ registros }) {

    return registros.map((registro, index) => {
        const { peticion, encargado, fecha, comentario, lista } = registro
        console.log(fecha.seconds)

        var date = new Date(fecha.seconds);
        var fechaString = "";
        var months_arr = ['01', '02', '03', '04', '05', '06', '07', '08', '09', '10', '11', '12'];
        var year = date.getFullYear();
        var month = months_arr[date.getMonth()];
        var day = date.getDate();
        fechaString = day + '/' + month + '/' + year;


        return (


            <Segment key={index}>
                <Grid columns="equal" >
                    <Grid.Column textAlign="left" >
                        <Header as='h3' textAlign='left'>
                            {peticion}
                        </Header>
                    </Grid.Column>
                    <Grid.Column  >
                        <Label as='a' color='orange' ribbon='right'>{fechaString}</Label>
                    </Grid.Column>
                </Grid>
                <Item>
                    <Grid columns="equal" >
                        <Grid.Column textAlign="left" >
                            <Header as="h4">{encargado}</Header>
                        </Grid.Column>
                        <Grid.Column textAlign="right" >
                            {lista}
                            </Grid.Column>
                    </Grid>
                    <Item.Description>
                        {comentario}
                            </Item.Description>
                    <Item.Extra>
                        <Button icon="file outline" ></Button>
                    </Item.Extra>
                </Item>
            </Segment>
        )
    })
}

export default ListarRegistros
