import React, { useState, useEffect } from 'react';
import Login from './login/Login.jsx';
import './App.css';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'

import Registro from './registro/Registro.jsx';
import Productos from './productos/Productos.jsx';
import Home from './home/Home.jsx';
import Datos from './datos_predictivos/Datos.jsx'
import Graficos from './graficos/Grafico.jsx'

function App() {

  const [isLogeado, setIsLogeado] = useState(true);

  useEffect(() => {

    if (!window.localStorage.getItem('token')) {
      setIsLogeado(false);
    }
  }, []);


  return (
    <Router>
      <Switch>
        <Route path="/login" exact component={isLogeado ? window.location = '/home': Login } />
        <Route path="/registro" exact component={isLogeado? Registro: Login} />

        <Route path="/home" exact component={isLogeado ? Home : Login} />
        <Route path="/graficos" exact component={isLogeado ? Graficos : Login} />
        <Route path="/datos" exact component={isLogeado ? Datos : Login} />

        <Route path="/productos" exact component={isLogeado ? Productos : Login} />
        <Route path="/" exact component={isLogeado ? Home : Login} />
      </Switch>
    </Router>
  );
}

export default App;
