import React, { useEffect, useState } from 'react'
import { ResponsiveBar } from '@nivo/bar'


import config from './ConfigBar'

import '../Chart.css'
import { getDatosGraficosBarra } from '../../firebase'
import { Button } from 'semantic-ui-react'

function Bar() {

    const [datos, setdatos] = useState([])
    const [booleano, setBol] = useState(false)
    const [key, setkey] = useState([])

    useEffect(() => {
        var tiempo = new Date();

        getDatosGraficosBarra(tiempo.getFullYear(), tiempo.getMonth(), (resp_datos) => {
            setdatos(resp_datos)
            console.log(resp_datos)
        }, (resp_key) => {
            setkey(resp_key)
            console.log(resp_key)

        })

    }, [])
    return (
        <div class="chart" >
            <Button onClick={e => setBol(!booleano)}>Click here</Button>
            {
                booleano &&
                <ResponsiveBar
                    data={datos}
                    keys={key}
                    indexBy="productos"
                    margin={config.margin}
                    padding={0.3}
                    groupMode="grouped"
                    colors={['Crimson',
                        '#ffae00',
                        'Crimson',
                        '#0066ff',
                        '#9000ff',
                        '#FFCA28']}
                    colorBy="id"
                    defs={config.defs}
                    borderWidth={2}
                    borderColor={{ from: 'color', modifiers: [['darker', '1.6']] }}
                    axisTop={null}
                    axisRight={null}
                    axisBottom={config.axisBottom}
                    axisLeft={config.axisLeft}
                    labelSkipWidth={12}
                    labelSkipHeight={12}
                    labelTextColor={{ from: 'color', modifiers: [['darker', 1.6]] }}
                    legends={config.legends}
                    animate={true}
                    onMouseOver={() => this.props.onMouseOverCallback()}
                    onMouseOut={() => this.props.onMouseOutCallback(null)}
                    motionStiffness={90}
                    motionDamping={15}
                />
            }

        </div>
    )
}
export default Bar;
