import _ from 'lodash'
import React, { Component } from 'react'
import { Table } from 'semantic-ui-react'

const datosTabla = [
    { codigo: 1,producto: 'John', cantidad: 15, stock: 20 ,ubicacion: 'Male' },
    { codigo: 2,producto: 'Amber', cantidad: 40,stock: 20  ,ubicacion: 'Female' },
    { codigo: 3,producto: 'Leslie', cantidad: 25, stock: 20 ,ubicacion: 'Other' },
    { codigo: 4,producto: 'Ben', cantidad: 70, stock: 20 ,ubicacion: 'Male' },
  ]

class Productos extends Component{

  constructor(props){
    super(props)
    this.state = {
        column: null,
        data: datosTabla,
        direction: null,
    }
  }  

    handleSort = (clickedColumn) => () => {
        const { column, data, direction } = this.state

        if (column !== clickedColumn) {
          this.setState({
            column: clickedColumn,
            data: _.sortBy(data, [clickedColumn]),
            direction: 'ascending',
          })

          return
    }
        this.setState({
            data: data.reverse(),
            direction: direction === 'ascending' ? 'descending' : 'ascending',
        })
    }


    render(){
        const { column, data, direction } = this.state

    return (
      <Table sortable celled fixed>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell
              sorted={column === 'codigo' ? direction : null}
              onClick={this.handleSort('codigo')}
            >
              Codigo
            </Table.HeaderCell>

            <Table.HeaderCell
              sorted={column === 'producto' ? direction : null}
              onClick={this.handleSort('producto')}
            >
              Producto
            </Table.HeaderCell>
            <Table.HeaderCell
              sorted={column === 'cantidad' ? direction : null}
              onClick={this.handleSort('cantidad')}
            >
              Cantidad
            </Table.HeaderCell>
            <Table.HeaderCell
              sorted={column === 'stock' ? direction : null}
              onClick={this.handleSort('stock')}
            >
              Stock
            </Table.HeaderCell>
            <Table.HeaderCell
              sorted={column === 'ubicacion' ? direction : null}
              onClick={this.handleSort('ubicacion')}
            >
              Ubicacion
            </Table.HeaderCell>
          </Table.Row>
        </Table.Header>
        <Table.Body>
          {_.map(data, ({ producto, cantidad, stock, ubicacion, codigo }) => (
            <Table.Row key={codigo}>
              <Table.Cell>{codigo}</Table.Cell>
              <Table.Cell>{producto}</Table.Cell>
              <Table.Cell>{cantidad}</Table.Cell>
              <Table.Cell>{stock}</Table.Cell>
              <Table.Cell>{ubicacion}</Table.Cell>
            </Table.Row>
          ))}
        </Table.Body>
      </Table>
    )
  }
}

export default Productos;
