import React, {useState, useEffect} from 'react'
import { ResponsivePie } from '@nivo/pie'

import './Chart.css'
import { getDatosHistoricosProductos } from '../../firebase'
function Grafico() {

    useEffect(() => {
        getDatosHistoricosProductos();
    }, [])

        return (
            <div class="chart" >

                <ResponsivePie
                    data={[
                        {
                            "id": "css",
                            "label": "css",
                            "value": 302,
                            "color": "hsl(235, 70%, 50%)"
                        },
                        {
                            "id": "java",
                            "label": "java",
                            "value": 223,
                            "color": "hsl(242, 70%, 50%)"
                        },
                        {
                            "id": "scala",
                            "label": "scala",
                            "value": 225,
                            "color": "hsl(209, 70%, 50%)"
                        },
                        {
                            "id": "python",
                            "label": "python",
                            "value": 528,
                            "color": "hsl(70, 70%, 50%)"
                        },
                        {
                            "id": "haskell",
                            "label": "haskell",
                            "value": 544,
                            "color": "hsl(7, 70%, 50%)"
                        }
                    ]}
                    margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
                    innerRadius={0.45}
                    padAngle={3}
                    cornerRadius={8}
                    colors={{ scheme: 'blues' }}
                    borderWidth={5}
                    borderColor="#787878"
                    // radialLabel={function (e) { return e.label }}
                    radialLabelsSkipAngle={25}
                    radialLabelsTextXOffset={0}
                    radialLabelsTextColor="#333333"
                    radialLabelsLinkOffset={-13}
                    radialLabelsLinkDiagonalLength={20}
                    radialLabelsLinkHorizontalLength={20}
                    radialLabelsLinkStrokeWidth={2}
                    radialLabelsLinkColor={{ from: 'color' }}
                    slicesLabelsSkipAngle={0}
                    slicesLabelsTextColor="#333333"
                    animate={true}
                    motionStiffness={90}
                    motionDamping={15}
                    defs={[
                        {
                            id: 'dots',
                            type: 'patternDots',
                            background: 'inherit',
                            color: 'rgba(255, 255, 255, 0.3)',
                            size: 4,
                            padding: 1,
                            stagger: true
                        },
                        {
                            id: 'lines',
                            type: 'patternLines',
                            background: 'inherit',
                            color: 'rgba(255, 255, 255, 0.3)',
                            rotation: -45,
                            lineWidth: 6,
                            spacing: 10
                        }
                    ]}
                />

            </div>
        )
    }

export default Grafico
